styles:
:host {
  display: flex;
  flex-direction: row;
  flex-wrap: nowrap;
  justify-content: space-between;
  padding: 0;
  vertical-align: middle;
}
.title {
  padding: 7px 25px 7px 0;
  font-family: Roboto;
  font-weight: 300;
  font-size: 1.55rem;
  font-weight: 500;
  vertical-align: middle;
  color: #3f4047;
}

:host{
    margin: 15px 0;
    margin-left: -20px;
    margin-right: -20px;
    border-bottom: 1px solid #f4f5f8;
    height: 0;
    overflow: hidden;
}

:host{
    display: table;
    width: 100%;
    vertical-align: middle;
    margin: 20px 20px 10px 20px;
}
.text{
    color:#2a61ac;
    display: table-cell;
    margin: 0;
    vertical-align: middle;
    font-weight: 600;
    font-size: 0.85rem;
    text-transform: uppercase;

}

:host{
    max-height: 500px;
    overflow-y: scroll;
    position: fixed !important;
    min-width: 200px;
    display: block;
    z-index:10;
    box-shadow: 0px 1px 2px -1px rgba(0, 0, 0, 0.2), 0px 4px 5px 0px rgba(0, 0, 0, 0.14), 0px 1px 10px 0px rgba(0, 0, 0, 0.12);
}

:host div a{
    display: block;
    text-align: left;
    padding:4px 10px;
    font-size:12px;
    md-icon{
        font-size:12px !important;
        height: 16px;
    }
}

:host div a md-icon{
    font-size:12px !important;
    height: 16px;
}
